INSERT INTO `guest_book_comments` VALUES (1,1,1,NULL,NULL,NULL,'이홈페이지는 2040년식 스타일입니다.\r\n너무 미래지향적이라 구식에 익숙하신분들은 이상하게 느낄수도 있습니다.\r\n약간만 보시면 왜 2040년식인지 알수있습니다.','2013-12-27 02:11:46','2013-12-27 02:11:46'),(2,2,1,NULL,NULL,NULL,'네 감사합니다.','2016-01-12 02:12:01','2016-01-12 02:12:01'),(3,3,1,NULL,NULL,NULL,'네, 다음에도 방문해주세요~ ^^','2016-01-19 02:12:01','2016-01-19 02:12:01'),(4,4,1,NULL,NULL,NULL,'네 감사합니다.\r\n계속 좋은 내용 만들어 가겠습니다.','2016-04-07 02:18:59','2016-04-07 02:18:59'),(5,5,1,NULL,NULL,NULL,'네~, 자주 방문해주세요~','2016-05-28 02:19:54','2016-05-28 02:19:54'),(6,6,1,NULL,NULL,NULL,'공감되었다니 기분 좋네요 \r\n간단한 일이라면 언제든지 의뢰하셔도 됩니다. ','2017-01-09 08:42:00','2017-01-09 08:42:00'),(7,7,1,NULL,NULL,NULL,'이름은 똑같지만 다른 프로그램이네요~\r\n이곳에서 따로 slboard라고 부르지는 않습니다.','2017-01-14 08:42:44','2017-01-14 08:42:44');
